const tenders = $('Get Tender').all().map(i => i.json).flat();
const competitors = $('Get Competitors').all().map(i => i.json).flat();
const bids = $input.all().map(i => i.json).flat();
const tender = tenders[0] || {};
const tender_id = $('Webhook').first().json.body.tender_id;
const org_id = $('Webhook').first().json.body.org_id;

// Build competitor profiles with history
const competitorProfiles = competitors.map(c => {
  const compBids = bids.filter(b => b.competitor_id === c.id);
  const wonBids = compBids.filter(b => b.won);
  return `- ${c.name} (ח.פ. ${c.company_number || 'לא ידוע'})
  תחומים: ${(c.typical_domains || []).join(', ')}
  חוזקות: ${(c.strengths || []).join(', ')}
  חולשות: ${(c.weaknesses || []).join(', ')}
  אסטרטגיית תמחור: ${c.known_pricing_strategy || 'לא ידוע'}
  מרווח רווח טיפוסי: ${c.typical_margin_range || 'לא ידוע'}
  היסטוריה: ${compBids.length} הצעות, ${wonBids.length} זכיות`;
}).join('\n\n');

const userMessage = `מפה את המתחרים הפוטנציאליים למכרז הבא וזהה את רמת האיום שלהם.

פרטי המכרז:
- שם: ${tender.tender_name || 'לא ידוע'}
- גוף מזמין: ${tender.issuing_body || 'לא ידוע'}
- תחום: ${tender.tender_type || 'לא ידוע'}
- שווי משוער: ${tender.estimated_value || 'לא צוין'}
- מועד הגשה: ${tender.submission_deadline || 'לא צוין'}

מתחרים מוכרים במערכת:
${competitorProfiles}

צור מיפוי מתחרים עם המבנה הבא (JSON):
{
  "competitors_analysis": [
    {
      "competitor_name": "שם המתחרה",
      "likelihood_to_bid": "HIGH/MEDIUM/LOW",
      "threat_level": "HIGH/MEDIUM/LOW",
      "strengths_for_tender": ["יתרון ספציפי למכרז זה"],
      "weaknesses_for_tender": ["חולשה ספציפית למכרז זה"],
      "estimated_bid_strategy": "תיאור אסטרטגיה צפויה",
      "our_counter_strategy": "איך לנטרל את האיום"
    }
  ],
  "market_summary": {
    "total_expected_bidders": 0,
    "competition_level": "HIGH/MEDIUM/LOW",
    "key_success_factors": ["גורם הצלחה 1"],
    "recommended_positioning": "המלצה לפוזיציה במכרז"
  }
}

JSON בלבד:`;

return [{
  json: {
    tender_id,
    org_id,
    tender_name: tender.tender_name,
    issuing_body: tender.issuing_body,
    competitors_count: competitors.length,
    userMessage
  }
}];

const claude = $input.first().json;
const prep = $('Prepare').first().json;

if (!claude.content) {
  return [{ json: { success: false, error: 'No Claude response', data: claude }}];
}

const text = claude.content[0]?.text || '';
let result = null;
let parseError = null;

try {
  const match = text.match(/\{[\s\S]*\}/s);
  if (match) result = JSON.parse(match[0]);
  else parseError = 'No JSON found';
} catch (e) {
  parseError = e.message;
}

return [{
  json: {
    success: result !== null,
    tender_id: prep.tender_id,
    tender_name: prep.tender_name,
    issuing_body: prep.issuing_body,
    competitors_in_db: prep.competitors_count,
    competitors_analysis: result?.competitors_analysis || [],
    market_summary: result?.market_summary || {},
    raw_response: text.substring(0, 500),
    parse_error: parseError,
    generated_at: new Date().toISOString()
  }
}];
