const tenders = $('Get Tender').all().map(i => i.json).flat();
const conditions = $input.all().map(i => i.json).flat();
const tender = tenders[0] || {};
const tender_id = $('Webhook').first().json.body.tender_id;
const org_id = $('Webhook').first().json.body.org_id;

// Calculate gate status
const meets = conditions.filter(c => c.status === 'MEETS').length;
const partial = conditions.filter(c => c.status === 'PARTIALLY_MEETS').length;
const fails = conditions.filter(c => c.status === 'DOES_NOT_MEET').length;
const unknown = conditions.filter(c => c.status === 'UNKNOWN' || !c.status).length;
const mandatory = conditions.filter(c => c.is_mandatory);
const mandatoryFails = mandatory.filter(c => c.status === 'DOES_NOT_MEET');

const conditionsText = conditions.slice(0, 15).map(c => 
  `${c.condition_number}: ${c.condition_text?.substring(0, 80)}... [${c.status || 'UNKNOWN'}] ${c.evidence || c.gap_description || ''}`
).join('
');

return [{
  json: {
    tender_id,
    org_id,
    tender_name: tender.tender_name || 'Unknown',
    deadline: tender.submission_deadline,
    estimated_value: tender.estimated_value,
    gate_stats: { total: conditions.length, meets, partial, fails, unknown, mandatory_fails: mandatoryFails.length },
    userMessage: `צור דוח החלטה GO/NO-GO למכרז.

פרטי המכרז:
- שם: ${tender.tender_name || 'לא ידוע'}
- מועד הגשה: ${tender.submission_deadline || 'לא צוין'}
- שווי משוער: ${tender.estimated_value || 'לא צוין'}

סטטיסטיקת תנאי סף:
- סה"כ: ${conditions.length}
- עומדים: ${meets}
- עומדים חלקית: ${partial}
- לא עומדים: ${fails}
- לא נבדקו: ${unknown}
- תנאי סף חובה שלא עומדים: ${mandatoryFails.length}

פירוט:
${conditionsText}

צור דוח החלטה עם המבנה הבא (JSON):
{
  "decision": "GO"/"NO-GO"/"CONDITIONAL",
  "confidence": 0-100,
  "executive_summary": "סיכום מנהלים (3-4 משפטים)",
  "eligibility_status": "ELIGIBLE"/"NOT_ELIGIBLE"/"CONDITIONAL",
  "blocking_issues": [{"issue": "תיאור", "severity": "CRITICAL", "resolution": "פתרון אפשרי"}],
  "strengths": ["חוזקה 1"],
  "weaknesses": ["חולשה 1"],
  "risks": [{"risk": "תיאור", "probability": "HIGH", "impact": "HIGH", "mitigation": "מענה"}],
  "recommended_actions": [{"action": "פעולה", "priority": "P1", "deadline": "זמן"}],
  "resource_estimate": {"bd_hours": 0, "tech_hours": 0, "estimated_cost": 0}
}

JSON בלבד:`
  }
}];

const claude = $input.first().json;
const prep = $('Prepare').first().json;

if (!claude.content) {
  return [{ json: { success: false, error: 'No Claude response', data: claude }}];
}

const text = claude.content[0]?.text || '';
let result = null;
let parseError = null;

try {
  const match = text.match(/\{[\s\S]*\}/s);
  if (match) result = JSON.parse(match[0]);
  else parseError = 'No JSON found';
} catch (e) {
  parseError = e.message;
}

return [{
  json: {
    success: result !== null,
    tender_id: prep.tender_id,
    tender_name: prep.tender_name,
    submission_deadline: prep.deadline,
    estimated_value: prep.estimated_value,
    gate_analysis: prep.gate_stats,
    decision: result?.decision || 'UNKNOWN',
    confidence: result?.confidence || 0,
    executive_summary: result?.executive_summary || '',
    eligibility_status: result?.eligibility_status || 'UNKNOWN',
    blocking_issues: result?.blocking_issues || [],
    strengths: result?.strengths || [],
    weaknesses: result?.weaknesses || [],
    risks: result?.risks || [],
    recommended_actions: result?.recommended_actions || [],
    resource_estimate: result?.resource_estimate || {},
    raw_response: text.substring(0, 400),
    parse_error: parseError,
    generated_at: new Date().toISOString()
  }
}];
