const tenders = $('Get Tender').all().map(i => i.json).flat();
const bids = $('Get Historical Bids').all().map(i => i.json).flat();
const pricing = $input.all().map(i => i.json).flat();
const tender = tenders[0] || {};
const tender_id = $('Webhook').first().json.body.tender_id;
const org_id = $('Webhook').first().json.body.org_id;

// Format historical bids
const bidsText = bids.map(b => {
  const pricePerUnit = typeof b.price_per_unit === 'string' ? JSON.parse(b.price_per_unit) : (b.price_per_unit || {});
  const unitPrices = Object.entries(pricePerUnit).map(([k,v]) => `${k}: ${v}`).join(', ');
  return `- ${b.tender_name} (${b.tender_year})
  מציע: ${b.competitors?.name || 'לא ידוע'}
  סכום: ${b.bid_amount?.toLocaleString()} ש"ח
  זכה: ${b.won ? 'כן' : 'לא'}
  מחירי יחידה: ${unitPrices || 'לא ידוע'}
  מרווח משוער: ${b.margin_estimate || 'לא ידוע'}%`;
}).join('\n\n');

// Format market pricing
const pricingText = pricing.map(p => 
  `- ${p.item_description} (${p.item_type})
  טווח: ${p.low_price}-${p.high_price} ש"ח/${p.unit}
  מחיר טיפוסי: ${p.typical_price} ש"ח
  מבוסס על: ${p.based_on_bids} הצעות`
).join('\n');

const userMessage = `נתח מודיעין תמחור למכרז הבא וספק המלצות לאסטרטגיית תמחור.

פרטי המכרז:
- שם: ${tender.tender_name || 'לא ידוע'}
- גוף מזמין: ${tender.issuing_body || 'לא ידוע'}
- שווי משוער: ${tender.estimated_value || 'לא צוין'}
- תחום: ${tender.tender_type || 'VIDEO'}

הצעות היסטוריות של מתחרים:
${bidsText || 'אין נתונים'}

מחירי שוק ממוצעים:
${pricingText || 'אין נתונים'}

צור ניתוח תמחור עם המבנה הבא (JSON):
{
  "pricing_analysis": {
    "estimated_market_range": {
      "low": 0,
      "typical": 0,
      "high": 0
    },
    "competitor_pricing_patterns": [
      {
        "competitor": "שם",
        "typical_strategy": "תיאור",
        "expected_bid_range": "טווח צפוי"
      }
    ],
    "key_cost_drivers": ["גורם עלות 1"]
  },
  "pricing_recommendation": {
    "recommended_bid_range": {
      "aggressive": 0,
      "balanced": 0,
      "conservative": 0
    },
    "recommended_strategy": "AGGRESSIVE/BALANCED/CONSERVATIVE",
    "strategy_rationale": "נימוק",
    "margin_target": "15-20%",
    "risk_factors": ["סיכון 1"]
  },
  "tactical_recommendations": [
    {
      "recommendation": "המלצה",
      "impact": "HIGH/MEDIUM/LOW",
      "implementation": "איך ליישם"
    }
  ]
}

JSON בלבד:`;

return [{
  json: {
    tender_id,
    org_id,
    tender_name: tender.tender_name,
    historical_bids_count: bids.length,
    market_prices_count: pricing.length,
    userMessage
  }
}];

const claude = $input.first().json;
const prep = $('Prepare').first().json;

if (!claude.content) {
  return [{ json: { success: false, error: 'No Claude response', data: claude }}];
}

const text = claude.content[0]?.text || '';
let result = null;
let parseError = null;

try {
  const match = text.match(/\{[\s\S]*\}/s);
  if (match) result = JSON.parse(match[0]);
  else parseError = 'No JSON found';
} catch (e) {
  parseError = e.message;
}

return [{
  json: {
    success: result !== null,
    tender_id: prep.tender_id,
    tender_name: prep.tender_name,
    historical_bids_analyzed: prep.historical_bids_count,
    market_prices_analyzed: prep.market_prices_count,
    pricing_analysis: result?.pricing_analysis || {},
    pricing_recommendation: result?.pricing_recommendation || {},
    tactical_recommendations: result?.tactical_recommendations || [],
    raw_response: text.substring(0, 500),
    parse_error: parseError,
    generated_at: new Date().toISOString()
  }
}];
