const tenders = $input.all().map(i => i.json).flat();
const tender = tenders[0] || {};
const tender_id = $('Webhook').first().json.body.tender_id;
const sow_text = $('Webhook').first().json.body.sow_text || '';

if (!sow_text && !tender.raw_data?.sow_text) {
  return [{ json: { tender_id, mode: 'NO_DATA', error: 'No SOW text available. Provide sow_text in request.' }}];
}

const text = sow_text || tender.raw_data?.sow_text || '';

return [{
  json: {
    tender_id,
    tender_name: tender.tender_name || 'Unknown',
    mode: 'ANALYZE',
    userMessage: `נתח את היקף העבודה (SOW) של המכרז וזהה חריגים, סיכונים ונקודות להבהרה.

היקף העבודה:
${text.substring(0, 4000)}

נתח את הטקסט וזהה:
1. תחומי עבודה עיקריים
2. לוחות זמנים
3. סיכונים (עומס עבודה, דרישות מיוחדות, תלות בגורמים חיצוניים)
4. דרישות חריגות
5. נקודות להבהרה

החזר JSON:
{
  "work_areas": [{"area": "תחום", "description": "תיאור", "complexity": "LOW/MEDIUM/HIGH"}],
  "timeline": {"total_months": 0, "milestones": [{"name": "שם", "deadline": "זמן"}]},
  "risks": [{"risk": "תיאור", "level": "HIGH", "mitigation": "פתרון מוצע"}],
  "exceptions": [{"item": "פריט", "issue": "הבעיה", "recommendation": "המלצה"}],
  "clarifications_needed": ["שאלה 1", "שאלה 2"],
  "overall_complexity": "LOW/MEDIUM/HIGH",
  "summary": "סיכום קצר"
}

JSON בלבד:`
  }
}];

const claude = $input.first().json;
const prep = $('Prepare').first().json;

if (prep.mode === 'NO_DATA') {
  return [{ json: prep }];
}

if (!claude.content) {
  return [{ json: { success: false, error: 'No Claude response', data: claude }}];
}

const text = claude.content[0]?.text || '';
let result = null;
let parseError = null;

try {
  const match = text.match(/\{[\s\S]*\}/s);
  if (match) result = JSON.parse(match[0]);
  else parseError = 'No JSON found';
} catch (e) {
  parseError = e.message;
}

return [{
  json: {
    success: result !== null,
    tender_id: prep.tender_id,
    tender_name: prep.tender_name,
    work_areas: result?.work_areas || [],
    timeline: result?.timeline || {},
    risks: result?.risks || [],
    risks_count: result?.risks?.length || 0,
    high_risks: (result?.risks || []).filter(r => r.level === 'HIGH').length,
    exceptions: result?.exceptions || [],
    clarifications_needed: result?.clarifications_needed || [],
    overall_complexity: result?.overall_complexity || 'UNKNOWN',
    summary: result?.summary || '',
    recommendation: result?.risks?.filter(r => r.level === 'HIGH').length > 2 
      ? 'REVIEW_CAREFULLY' 
      : 'PROCEED',
    raw_response: text.substring(0, 400),
    parse_error: parseError,
    timestamp: new Date().toISOString()
  }
}];
