// Prepare data for Claude to generate clarification questions
const conditions = $input.first().json;
const webhookData = $('Webhook').first().json.body;
const tender_id = webhookData.tender_id;
const org_id = webhookData.org_id;

// Filter conditions that need clarification (UNKNOWN, DOES_NOT_MEET, or gaps)
const conditionsNeedingClarification = conditions.filter(c => 
  c.status === 'UNKNOWN' || 
  c.status === 'DOES_NOT_MEET' || 
  c.status === 'PARTIALLY_MEETS' ||
  c.gap_description
);

const conditionsText = conditions.map(c => 
  `סעיף ${c.condition_number}: ${c.condition_text}\nסטטוס: ${c.status || 'PENDING'}\nפער: ${c.gap_description || 'אין'}`
).join('\n\n');

const systemPrompt = `אתה יועץ מכרזים מומחה בישראל. תפקידך ליצור שאלות הבהרה למזמין המכרז.

כללים:
1. שאלות הבהרה מנוסחות כ"בקשה" - "נבקש להבהיר..."
2. כל שאלה חייבת להתייחס לסעיף ספציפי במכרז
3. סווג כל שאלה לפי עדיפות:
   - P1: משנה GO/NO-GO
   - P2: משנה רמת סיכון
   - P3: מחזק טיעון
4. ציין את המטרה של כל שאלה (מה אנחנו מנסים להשיג)
5. הצע ניסוח שאלה שמיטיב עם המציע

החזר JSON array עם המבנה:
{
  "question_number": מספר,
  "related_condition": "מספר סעיף",
  "question_text": "נוסח השאלה",
  "priority": "P1"/"P2"/"P3",
  "purpose": "מטרת השאלה",
  "desired_outcome": "התוצאה הרצויה",
  "question_type": "CLARIFICATION"/"EXPANSION"/"RELAXATION"
}`;

return [{
  json: {
    tender_id,
    org_id,
    conditions: conditions,
    conditionsText,
    systemPrompt,
    userMessage: `נתח את תנאי הסף הבאים וצור שאלות הבהרה למזמין. התמקד בתנאים שלא עומדים בהם או שיש בהם אי-בהירות:\n\n${conditionsText}`
  }
}];

// Parse Claude's response
const claudeResponse = $input.first().json;
const prepData = $('Prepare for Claude').first().json;

if (!claudeResponse.content || !claudeResponse.content[0]) {
  return [{ json: { success: false, error: 'No response from Claude' }}];
}

const responseText = claudeResponse.content[0].text || '';

// Extract JSON
let jsonText = responseText;
const jsonMatch = responseText.match(/```json\s*([\s\S]*?)\s*```/);
if (jsonMatch) jsonText = jsonMatch[1];
else {
  const arrayMatch = responseText.match(/\[\s*\{[\s\S]*\}\s*\]/);
  if (arrayMatch) jsonText = arrayMatch[0];
}

let questions;
try {
  questions = JSON.parse(jsonText);
} catch (e) {
  return [{ json: { success: false, error: 'Parse error', raw: responseText.substring(0, 500) }}];
}

// Categorize questions
const p1Questions = questions.filter(q => q.priority === 'P1');
const p2Questions = questions.filter(q => q.priority === 'P2');
const p3Questions = questions.filter(q => q.priority === 'P3');

return [{
  json: {
    success: true,
    tender_id: prepData.tender_id,
    total_questions: questions.length,
    summary: {
      p1_critical: p1Questions.length,
      p2_important: p2Questions.length,
      p3_nice_to_have: p3Questions.length
    },
    questions: questions,
    by_priority: {
      P1: p1Questions,
      P2: p2Questions,
      P3: p3Questions
    },
    next_step: 'REVIEW_AND_SUBMIT',
    message: `נוצרו ${questions.length} שאלות הבהרה. ${p1Questions.length} קריטיות (P1)`,
    timestamp: new Date().toISOString()
  }
}];
