const conditions = $input.all().map(i => i.json).flat();
const tender_id = $('Webhook').first().json.body.tender_id;

const conditionsText = conditions.map(c => 
  `${c.condition_number}: ${c.condition_text} [סטטוס: ${c.status || 'PENDING'}]`
).join('
');

return [{
  json: {
    tender_id,
    conditions_count: conditions.length,
    userMessage: `צור רשימת מסמכים נדרשים להגשת מכרז.

תנאי סף:
${conditionsText}

עבור כל תנאי סף, פרט:
1. שם המסמך
2. סוג: FINANCIAL (פיננסי), TECHNICAL (טכני), LEGAL (משפטי), CERTIFICATE (תעודה), REFERENCE (המלצה), FORM (טופס)
3. מקור: COMPANY (מהחברה), EXTERNAL (חיצוני - רו"ח, עו"ד), CUSTOMER (לקוח), AUTHORITY (רשות)
4. זמן הכנה: ימים
5. חובה/אופציונלי

החזר JSON array בלבד:
[{"condition": "1", "doc_name": "שם המסמך", "doc_type": "FINANCIAL", "source": "EXTERNAL", "prep_days": 5, "mandatory": true, "notes": "הערות"}]

JSON בלבד:`
  }
}];

const claude = $input.first().json;
const prep = $('Prepare').first().json;

if (!claude.content) {
  return [{ json: { success: false, error: 'No Claude response', data: claude }}];
}

const text = claude.content[0]?.text || '';
let docs = [];
let parseError = null;

try {
  const match = text.match(/\[\s*\{[\s\S]*\}\s*\]/s);
  if (match) docs = JSON.parse(match[0]);
  else parseError = 'No JSON array found';
} catch (e) {
  parseError = e.message;
}

// Group by type
const byType = {};
for (const d of docs) {
  const t = d.doc_type || 'OTHER';
  if (!byType[t]) byType[t] = [];
  byType[t].push(d);
}

// Calculate timeline
const maxDays = Math.max(...docs.map(d => d.prep_days || 0));
const mandatory = docs.filter(d => d.mandatory);
const external = docs.filter(d => d.source === 'EXTERNAL' || d.source === 'CUSTOMER' || d.source === 'AUTHORITY');

return [{
  json: {
    success: docs.length > 0,
    tender_id: prep.tender_id,
    total_documents: docs.length,
    mandatory_count: mandatory.length,
    external_count: external.length,
    max_prep_days: maxDays,
    by_type: byType,
    documents: docs,
    timeline: {
      recommended_start_days_before: maxDays + 5,
      critical_path: external.map(d => ({ doc: d.doc_name, days: d.prep_days, source: d.source }))
    },
    raw_response: text.substring(0, 400),
    parse_error: parseError,
    timestamp: new Date().toISOString()
  }
}];
